value.css:
section {
	color: #6B7280;
  position: relative;
  max-width: 1280px;
  padding: 0 2rem;
}

figure {
  position: relative;

  svg {
    position: absolute;
    top: 0;
  	bottom: 0;
    left: 0;
  	width: 100%;
  	height: 100%;
  	z-index: 0;
  	padding-top: 3rem;
  	padding-right: 3rem;

    rect {
      width: 100%;
      height: 100%;
      fill: currentcolor;
    }
  }

  img {
    width: 100%;
    position: relative;
    padding-left: 1rem;
  	padding-bottom: 1rem;
  }
}

.body {
  background: white;

  h1 {
    padding: 3rem;
  }

  .superhead {
    font-size: 1rem;
  	text-transform: uppercase;
  	display: flex;
    flex-direction: column;
  }

  .heading {
    font-size: 3rem;
    text-transform: capitalize;
  	font-weight: 500;
  	line-height: 3rem;
  }
}

@media screen and (min-width: 1000px) {
  .body {
    position: absolute;
    bottom: 0;
    left: 0;

    h1 {
      margin-bottom: 1.5rem;
    }
  }
  img {
    padding-left: 3rem;
    padding-bottom: 3rem;
  }
}

header {
  background: #222;
  display: flex;
  position: relative;

  &:before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    opacity: 0.25;
  }

  .container {
    z-index: 10;

    .content {
      --tw-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
			box-shadow: var(--tw-ring-offset-shadow, 0 0 #0000), var(--tw-ring-shadow, 0 0 #0000), var(--tw-shadow);
    	background: #222;
    	color: white;
    	text-align: center;
    	padding: 3rem 4rem;
      margin: 6rem 0;
      width: 100%;
      max-width: 600px;

      h1 {
        margin-bottom: 3rem;
        display: flex;
        flex-direction: column;

        span:first-child {
          font-weight: 600;
          margin-bottom: 0.5rem;
        }

        span:last-child {
        	font-size: 3.75rem;
        	font-weight: 600;
          line-height: 1.2;
        }
      }

      a {
        border-bottom: 2px solid #333;
      	font-weight: 700;
        padding-bottom: 0.25rem;
      	margin: 0 0.5rem;
        transition: border-color 0.1s;

        &:hover {
          border-color: white;
        }
      }
  	}
  }

    img {
      position: absolute;
      width: 75%;
    	right: 0;
    	height: 100%;
    	z-index: 0;
    	object-position: left;
    	object-fit: cover;
    }
}

